mapper.update:
update user
set my_name = #{myName,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  mobile = #{mobile,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=BIGINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, my_name, age, mobile, create_time
from user
where id = #{id,jdbcType=INTEGER}
